import '../Styles/flies.css'
import React, { useState } from "react";
import FlyPopUp from './FlyPopUp';
import Search from './Search';

function Flies({flies}) {

    const [selectedType, setSelectedType] = useState('')
    const [selectedFly, setSelectedFly] = useState([])
    const [selectedCycle, setSelectedCycle] = useState('')
    const [selectedSpecies, setSelectedSpecies] = useState('')
    const [searchValue, setSearchValue] = useState('')
    const [searchResult, setSearchResult] = useState([])

    function filterFlies() {

        let sortedFlies

        const filters = {
            'Dry Flies': {
                '': {
                    '': fly => fly.fly_type === 'Dry',
                    'Mayfly': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Mayfly',
                    'Caddis': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Caddis',
                    'Stonefly': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Stonefly',
                    'Midge': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Midge',
                    'Terrestrials': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Terrestrial',
                    'Baitfish': fly => fly.fly_type === 'Dry' && fly.fly_imitation === 'Baitfish'
                },
                'Nymph': {
                    '': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph',
                    'Mayfly': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Mayfly',
                    'Caddis': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Caddis',
                    'Stonefly': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Stonefly',
                    'Midge': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Midge',
                    'Terrestrials': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Terrestrial',
                    'Baitfish': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Nymph' && fly.fly_imitation === 'Baitfish'
                },
                'Emerger': {
                    '': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Emerger',
                    'Mayfly': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Emerger' && fly.fly_imitation === 'Mayfly',
                    'Caddis': fly => fly.fly_type === 'Dry' && fly.fly_life_cycle === 'Emerger' && fly.fly_imitation === 'Caddis',
