import '../Styles/home.css'
import { useState, useEffect } from 'react';

function Home({flies}) {

    const [featuredFlies, setFeaturedFlies] = useState([])

    useEffect(() => {
        const selectedFlies = selectThreeRandomFlies();
        setFeaturedFlies(selectedFlies);
    }, {flies});

    function selectThreeRandomFlies() {
        if (flies.length < 3) {
            throw new Error('List must contain at least three items');
          }

        while (featuredFlies.length < 3) {
          const randomIndex = Math.floor(Math.random() * flies.length); // Generate a random index
          const randomObject = flies[randomIndex]; // Get the object at the random index

          // Check if the object is not already selected
          if (!featuredFlies.includes(randomObject)) {
            featuredFlies.push(randomObject); // Add the object to the selected objects array
          }
        }

        return setFeaturedFlies;
      }

  return (
    <div>
      {/* Header Section */}
      <header className="header">
        <h1>Welcome to Hatch Match</h1>
        <img className='logo' src='https://media.istockphoto.com/id/1292289580/vector/dry-fly.jpg?s=612x612&w=0&k=20&c=oNnjoO5_t9hPTSczXk3j38TNvJCgymmlXCbGL7Bnha8=' />    
      </header>

      {/* Featured Flies Section */}
      <section className="featured-flies">
        <div className="container">
            {featuredFlies.map((fly)=> {
                return(
                    <div key={fly.id} className="fly-card">
                        <h2>{fly.fly_name}</h2>
                        <img src={fly.fly_image} alt='Image not available'/>
                        <h3>Target Species: {fly.species_names}</h3>
                    </div>
                    )
                })} 
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="testimonials">
        <div className="container">
          <div className="testimonial">
            <p>Customer Testimonial 1</p>
          </div>
          <div className="testimonial">
            <p>Customer Testimonial 2</p>
          </div>
          {/* Add more testimonial components as needed */}
        </div>
      </section>

      {/* Footer Section */}
      <footer>
        <div className="container">
          <ul>
            <li><a href="#">Terms of Service</a></li>
            <li><a href="#">Privacy Policy</a></li>
            <li><a href="#">FAQs</a></li>
          </ul>
        </div>
      </footer>
    </div>
  );
}

export default Home;
