import '../Styles/flies.css'
import React, { useState } from "react";
import FlyPopUp from './FlyPopUp';

function Flies({flies}) {

    const [selectedType, setSelectedType] = useState('')
    const [selectedFly, setSelectedFly] = useState([])
    const [selectedCycle, setSelectedCycle] = useState('')

    function filterFliesType() {
        let sortedFlies;

        if(selectedType === 'Dry Flies' && selectedCycle === '') {
            sortedFlies = [...flies].filter(fly => fly.fly_type == 'Dry')
        } else if(selectedType === 'Dry Flies' && selectedCycle === 'Nymph') {
            sortedFlies = [...flies].filter(fly => fly.fly_type == "Dry" && fly.fly_life_cycle == "Nymph")
        } else if(selectedType === 'Wet Flies') {
            sortedFlies = [...flies].filter(fly => fly.fly_type == 'Wet')
        } else if (selectedType === 'Steamers') {
            sortedFlies = [...flies].filter(fly => fly.fly_type == 'Streamer')
        } else if (selectedType === 'Emergers') {
            sortedFlies = [...flies].filter(fly => fly.fly_life_cycle == 'Emerger')
        }
        else {
            sortedFlies = flies
        }

        return sortedFlies
    }

    const flyInfo = filterFliesType();

    function handleTypeChange(e) {
        setSelectedType(e.target.value);
    }

    function handleCycleChange(e) {
        setSelectedCycle(e.target.value);
    }

    const [isOpen, setIsOpen] = useState(false);

    const togglePopup = () => {
      setIsOpen(!isOpen);
      console.log(selectedFly)
    };

    return (
        <div>
            <header>Flies</header>
            <div className='filter_type'>
                <h2>Fly types</h2>
                <select value={selectedType} onChange={handleTypeChange}>
                    <option value=''> All </option>
                    <option value='Dry Flies'>Dry Flies</option>
                    <option value='Wet Flies'>Wet Flies</option>
                    <option value='Steamers'>Steamers</option>
                    <option value='Emergers'>Emergers</option>
                </select>
            </div>
            <div className='filter_cycle'>
                <h2>Life Cycle</h2>
                <select value={selectedCycle} onChange={handleCycleChange}>
                    <option value=''> All </option>
                    <option value='Nymph'>Nymph</option>
                    <option value='Emerger'>Emerger</option>
                    <option value='Adult'>Adult</option>
                </select>
            </div>
            <div>
                <FlyPopUp isOpen={isOpen} togglePopup={togglePopup} selectedFly={selectedFly}/>
            </div>
            <div className='fly-grid'>
                {flyInfo.map((fly)=> {
                    return(
                        <div key={fly.id} className="fly-card" onClick={() => { togglePopup(); setSelectedFly(fly)}}>
                            <h2 onClick={togglePopup}>{fly.fly_name}</h2>
                            <img src={fly.fly_image} alt='Image not available'/>
                            <h3>{fly.fly_imitation}</h3>
                            <h3>{fly.fly_type}</h3>
                        </div>
                    )
                })}
            </div>
        </div>    
    )
}

export default Flies;
