import React, {useEffect, useState} from "react";
import '../Styles/Search.css'

function Search() {
    const [selectedState, setSelectedState] = useState('');
    const [selectedRegion, setSelectedRegion] = useState('')
    const [selectedSeason, setSelectedSeason] = useState('')

    const handleStateChange = (event) => {
      setSelectedState(event.target.value);
    };

    const handleRegionChange = (event) => {
        setSelectedRegion(event.target.value)
    }

    const handleSeasonChange = (event) => {
        setSelectedSeason(event.target.value)
    }

    // Array of 50 states in alphabetical order
    const states = [
      'Alabama', 'Alaska', 'Arizona', 'Arkansas', 'California', 'Colorado', 'Connecticut', 'Delaware', 'Florida',
      'Georgia', 'Hawaii', 'Idaho', 'Illinois', 'Indiana', 'Iowa', 'Kansas', 'Kentucky', 'Louisiana', 'Maine', 'Maryland',
      'Massachusetts', 'Michigan', 'Minnesota', 'Mississippi', 'Missouri', 'Montana', 'Nebraska', 'Nevada', 'New Hampshire',
      'New Jersey', 'New Mexico', 'New York', 'North Carolina', 'North Dakota', 'Ohio', 'Oklahoma', 'Oregon', 'Pennsylvania',
      'Rhode Island', 'South Carolina', 'South Dakota', 'Tennessee', 'Texas', 'Utah', 'Vermont', 'Virginia', 'Washington',
      'West Virginia', 'Wisconsin', 'Wyoming'
    ];

    return (
      <div>
        <label htmlFor="dropdown">Select a state: </label>
        <select id="state-dropdown" value={selectedState} onChange={handleStateChange}>
          <option value="">Choose a state</option>
          {states.map((state, index) => (
            <option key={index} value={state}>{state}</option>
          ))}
        </select>
        <label htmlFor="dropdown">Select a region: </label>
        <select id="region-dropdown" value={selectedRegion} onChange={handleRegionChange} >
            <option value=''>Choose a Region</option>
            <option>North East</option>
            <option>North West</option>
            <option>Central</option>
            <option>South East</option>
            <option>South West</option>
        </select>
        <label>Select a season: </label>
        <select id="season-dropdown" value={selectedSeason} onChange={handleRegionChange}>
            <option>Choose a Season </option>
            <option>Spring </option>
            <option>Summer </option>
            <option>Fall </option>
            <option>Winter </option>
        </select>

        <button>Search</button>

      </div>
    );
}

export default Search;
