import '../Styles/home.css';
import { useState, useEffect } from 'react';

function Home({ flies }) {
    const [featuredFlies, setFeaturedFlies] = useState([]);

    useEffect(() => {
        if (flies.length > 0) {
            const selectedFlies = selectThreeRandomFlies();
            setFeaturedFlies(selectedFlies);
        }
    }, [flies]);

    function selectThreeRandomFlies() {
        const selectedFlies = [];
        const availableFlies = [...flies];

        while (selectedFlies.length < 3 && availableFlies.length > 0) {
            const randomIndex = Math.floor(Math.random() * availableFlies.length);
            const randomFly = availableFlies.splice(randomIndex, 1)[0];
            selectedFlies.push(randomFly);
        }

        return selectedFlies;
    }

    return (
        <div>
            <header className="header">
                <h1>Welcome to Hatch Match</h1>
                <img className='logo' src='https://media.istockphoto.com/id/1292289580/vector/dry-fly.jpg?s=612x612&w=0&k=20&c=oNnjoO5_t9hPTSczXk3j38TNvJCgymmlXCbGL7Bnha8=' alt='Hatch Match Logo' />
            </header>

            <section className="featured-flies">
                <div className="container">
                    {featuredFlies.map((fly, index) => (
                        <div key={index} className="fly-card">
                            <h2>{fly?.fly_name}</h2>
                            <img src={fly?.fly_image} alt='Image not available' />
                            <h3>Target Species: {fly?.species_names}</h3>
                        </div>
                    ))}
                </div>
            </section>

            <section className="testimonials">
                <div className="container">
                    <div className="testimonial">
                        <p>Customer Testimonial 1</p>
                    </div>
                    <div className="testimonial">
                        <p>Customer Testimonial 2</p>
                    </div>
                </div>
            </section>

            <footer>
                <div className="container">
                    <ul>
                        <li><a href="#">Terms of Service</a></li>
                        <li><a href="#">Privacy Policy</a></li>
                        <li><a href="#">FAQs</a></li>
                    </ul>
                </div>
            </footer>
        </div>
    );
}

export default Home;
